package com.practice.springProj.Service;

import com.practice.springProj.Service.Interfaces.ConcatSubstringsService;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;
import org.springframework.boot.test.context.SpringBootTest;

import java.util.Arrays;
import java.util.Collections;
import java.util.List;
import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.*;
@SpringBootTest
class ConcatSubstringsServiceImplTest {
    ConcatSubstringsService concatSubstringsService;

    @BeforeEach
    void setUp() {
        concatSubstringsService = new ConcatSubstringsServiceImpl();
    }

//    @AfterEach
//    void tearDown() {
//    }

    private static Stream<Arguments> provideConcatSubstringTestParams() {
        return Stream.of(
                Arguments.of("barfoothefoobarman", new String[]{"foo","bar"}, List.of(0,9)),
                Arguments.of("wordgoodgoodgoodbestword", new String[]{"word","good","best","word"}, List.of()),
                Arguments.of("barfoofoobarthefoobarman", new String[]{"bar","foo","the"}, List.of(6,9,12)),
                Arguments.of("lingmindraboofooowingdingbarrwingmonkeypoundcake", new String[]{"fooo","barr","wing","ding","wing"}, List.of(13)),
                Arguments.of("",
                        new String[]{"dhvf","sind","ffsl","yekr","zwzq","kpeo","cila","tfty","modg","ztjg","ybty","heqg","cpwo","gdcj","lnle","sefg","vimw","bxcb"},
                        List.of(935))
                );
    }

    @ParameterizedTest
    @MethodSource("provideConcatSubstringTestParams")
    void testFindSubstring(String s, String[] words, List<Integer> expected) {
        List<Integer> actual = concatSubstringsService.findSubstring(s, words);
        assertEquals(expected, actual);
    }
}